library(shiny)

ui <- fluidPage(
  
  #  METADATOS 
  titlePanel("Simulaci贸n de un dado"),
  
  tags$head(
    tags$style(HTML("
      .info-box {
        background-color: #f5f7fa;
        padding: 15px;
        border-radius: 8px;
        margin-bottom: 20px;
      }
    "))
  ),
  
  fluidRow(
    column(
      12,
      div(
        class = "info-box",
        h4(" Enunciado"),
        p("Vamos a simular el lanzamiento de un dado usando el ordenador."),
        p("Elegiremos cu谩ntas veces tiramos el dado y observaremos, mediante un histograma,"),
        p("con qu茅 frecuencia aparece cada una de las seis caras."),
        br(),
        h4("癸 Informaci贸n"),
        tags$ul(
          tags$li("Curso: 2潞 ESO"),
          tags$li("Asignatura: Matem谩ticas"),
          tags$li("Conceptos : Ley de los grandes n煤meros"),
          tags$li("Lenguaje de programaci贸n: R")
        ),
        br(),
        p(strong("Autor:"), "Departamento de Matem谩ticas"),
        p(strong("Centro:"), "IES Liceo"),
        p(strong("Actividad :"), "Simulaci贸n interactiva")
      )
    )
  ),
  
  sidebarLayout(
    sidebarPanel(
      numericInput(
        inputId = "size",
        label = " Cantidad de tiradas del dado",
        value = 10000,
        min = 10,
        max = 100000,
        step = 100
      ),
      helpText("Prueba con distintos valores y observa c贸mo cambia el histograma.")
    ),
    
    mainPanel(
      plotOutput("histograma")
    )
  )
)

server <- function(input, output) {
  
  # Funci贸n que genera los resultados
  generar_resultados <- function(size) {
    sample(1:6, size = size, replace = TRUE)
  }
  
  # Reactivo
  resultados <- reactive({
    req(input$size)
    generar_resultados(input$size)
  })
  
  # Histograma
  output$histograma <- renderPlot({
    hist(
      resultados(),
      breaks = seq(0.5, 6.5, by = 1),
      col = "steelblue",
      border = "white",
      main = "Resultados de las tiradas del dado",
      xlab = "Cara del dado",
      ylab = "Frecuencia"
    )
  })
}

shinyApp(ui = ui, server = server)
